    /**
     * herokuで公開している運行情報を取得するための非同期タスク
     */
    private class AsyncHttpRequest extends AsyncTask<Uri.Builder, Void, String> {

        /** デバッグ用タグ */
        private static final String TAG = "AsyncHttpRequest";
        /** デバッグ用フラグ */
        private static final boolean DEBUG = false;

        /** 呼び出し元コンテキスト */
        private Context context;
        /** 呼び出し元Activity */
        private Activity mainActivity;

        /** XML 扱うためのファクトリ */
        private DocumentBuilderFactory dbFactory;
        /** XML 扱うためのビルダー */
        private DocumentBuilder xmlbuilder;

        /** 休日・平日フラグ */
        private String holiday;
        /** モノちゃん号の運行情報 */
        private String Service0;
        /** アーバンフライ０ 1-2号の運行情報 */
        private String Service1;
        /** アーバンフライ０ 3-4号の運行情報 */
        private String Service2;
        /** アーバンフライ０ 5-6号の運行情報 */
        private String Service3;
        /** アーバンフライ０ 7-8号の運行情報 */
        private String Service4;

        public AsyncHttpRequest(Context context) {

        }

        /**
         * 非同期処理本体
         * @param builders ハンドラ
         * @return onPostExecuteへ伝える引数
         */
        @Override
        protected String doInBackground(Uri.Builder... builders) {
            try {
                dbFactory = DocumentBuilderFactory.newInstance();
                xmlbuilder = dbFactory.newDocumentBuilder();
                Document document = xmlbuilder.parse("http://monoview.herokuapp.com/today.xml");

                XPathFactory factory = XPathFactory.newInstance();
                XPath xpath = factory.newXPath();

                holiday = xpath.evaluate("//holiday[1]/text()", document);
                Service0 = xpath.evaluate("/tables/table[train ='0']/table/text()", document);
                Service1 = xpath.evaluate("/tables/table[train ='1']/table/text()", document);
                Service2 = xpath.evaluate("/tables/table[train ='2']/table/text()", document);
                Service3 = xpath.evaluate("/tables/table[train ='3']/table/text()", document);
                Service4 = xpath.evaluate("/tables/table[train ='4']/table/text()", document);

            } catch (SAXException e) {
                return "false";
            } catch (IOException e) {
                return "false";
            } catch (ParserConfigurationException e) {
                return "false";
            } catch (XPathExpressionException e) {
                return "false";
            }

            if (DEBUG) Log.d(TAG, "result: " + holiday);

            return "true";

        }

        /**
         * 非同期処理の結果を処理
         * @param result 非同期処理の結果の引数　成否判定
         */
        @Override
        protected void onPostExecute(String result) {
            if (result.equals("false")) intHoliday = 0;
            //休日・平日判定
            if (holiday.equals("true")) {
                intHoliday = 0;
            } else if (holiday.equals("false")) {
                intHoliday = 1;
            } else {
                //取得失敗
                intHoliday = 1;
            }
            intService0 = Service0.matches("[0-9]+")?Integer.valueOf(Service0):-1;
            intService1 = Service1.matches("[0-9]+")?Integer.valueOf(Service1):-1;
            intService2 = Service2.matches("[0-9]+")?Integer.valueOf(Service2):-1;
            intService3 = Service3.matches("[0-9]+")?Integer.valueOf(Service3):-1;
            intService4 = Service4.matches("[0-9]+")?Integer.valueOf(Service4):-1;

        }
    }

    /**
     * マップ画面に定期的[10s]に画面更新をするためのハンドラ
     */
    private class TrainHandler extends Handler {
        @Override
        public void handleMessage(Message msg) {
            if (trainHandler != null){
                getLoaderManager().restartLoader(intHoliday, null, callbacks);
                trainHandler.sleep(500); //0.5s
            }
        }

        //スリープメソッド
        public void sleep(long delayMills) {
            //使用済みメッセージの削除
            removeMessages(0);
            sendMessageDelayed(obtainMessage(0), delayMills);
        }
    }


